import { useState, useEffect, useContext } from "react"
import PopupWithForm from "./PopupWithForm"
//import { CurrentUserContext } from "../contexts/CurrentUserContext"
//import Popup from "./Popup"

function EditProfilePopup(props) {
    const { loader, isOpen, onClose, onUpdateUser } = props

//    const currentUser = useContext(CurrentUserContext)

//    useEffect(() => {
//        setName(currentUser.name || '');
//        setDescription(currentUser.about || '');
//    }, [currentUser])

//    const [name, setName] = useState('')
//    const [description, setDescription] = useState('')

//    function handleSubmit(evt) {
//        evt.preventDefault()
//        onUpdateUser({
//            name,
 //           about: description
//        })
//    }

    return (
            <PopupWithForm
                onClose={onClose}
                isOpen={isOpen}
                name="edit"
                title="Редактировать профиль"
                buttonText={loader ? "Сохранение..." : "Сохранить"}
            //    onSubmit={handleSubmit}
            >
                <label className="popup__form-label">
                    <input className="popup__input popup__input_value_name" id="name-input" type="text" name="name" required minLength="2" maxLength="40" autoComplete="off" placeholder="Ваше имя"/>
                    <span className="popup__input-error name-input-error"></span>
                </label>
                <label className="popup__form-label">
                    <input className="popup__input popup__input_value_about" id="about-input" type="text" name="about" required minLength="2" maxLength="200" autoComplete="off" placeholder="О себе"/>
                    <span className="popup__input-error about-input-error"></span>
                </label>
            </PopupWithForm>
    )
}

export default EditProfilePopup
