function Main() {

    

    return (
        <main className="main">

          <section className="profile">
            <button className="profile__avatar-box" onClick={handleEditAvatarClick}>
              <img className="profile__avatar" src="./images/No_avatar.svg" alt="Аватар"/>
            </button>
                <div className="profile__info">
                   <div className="profile__name-container">
                     <h1 className="profile__name">Жаклин Кустодиев</h1>
                     <button className="profile__edit-button link" type="button" onClick={handleEditProfileClick} aria-label="Редактировать профиль"></button>
                   </div>
                   <p className="profile__job">Искатель лайков</p>
                </div>
              <button className="profile__add-button link" type="button" onClick={handleAddPlaceClick} aria-label="Добавить"></button>
          </section>
          
          <section className="elements"></section>

          
      
      <template className="item__template" id="card">
        <article className="element">
          <div className="element__image-container">
            <button className="element__trash" type="button" aria-label="Удалить"></button>
            <img className="element__image" src="#" alt="Фотография места"/>
          </div>
          <div className="element__info-container">
            <h2 className="element__name"></h2>
            <div className="element__like-container">
              <button className="element__like" type="button" aria-label="нравится"></button>
              <p className="element__like-counter">0</p>
            </div>
          </div>
        </article>
      </template>

        </main>
    )
}

const popupEditProfile = document.querySelector('.popup_edit-form');
    console.log(popupEditProfile)
    
    const popupAddPlace = document.querySelector('.popup_add-form');
    console.log(popupAddPlace)
    
    const popupAvatar = document.querySelector('.popup_apdate-avatar');
    console.log(popupAvatar)
    
    function handleEditAvatarClick() {
        popupAvatar.classList.add('popup_opened');
      }
    
    function handleEditProfileClick() {
        popupEditProfile.classList.add('popup_opened');
      }
    
    function handleAddPlaceClick() {
        popupAddPlace.classList.add('popup_opened');
      }


export default Main
