//import { useContext } from "react"
import { optionsApi } from "../components/Utils/optionsApi"
import Api from "../components/Utils/Api"
import Card from "./Card"

const api = new Api(optionsApi)

function Main(props) {
    const { onEditProfile, onAddPlace, onEditAvatar, onCardClick, cards, onCardLike, onCardDelete } = props

    console.log(Api)
  //  const currentUser = useContext(CurrentUserContext)
    
    return (
        <main className="main">
          <section className="profile">
            <button className="profile__avatar-box" onClick={onEditAvatar}>
              <img className="profile__avatar" src="./images/No_avatar.svg" alt="Аватар"/>
            </button>
                <div className="profile__info">
                   <div className="profile__name-container">
                     <h1 className="profile__name">Жак Кусто{/*currentUser.name*/}</h1>
                     <button className="profile__edit-button link" type="button" onClick={onEditProfile} aria-label="Редактировать профиль"></button>
                   </div>
                   <p className="profile__job">Искатель лайков{/*currentUser.about*/}</p>
                </div>
              <button className="profile__add-button link" type="button" onClick={onAddPlace} aria-label="Добавить"></button>
          </section>

          <section className="elements">
                {
                    cards.map(card => (
                        <Card
                            key={card._id}
                            card={card}
                            onCardClick={onCardClick}
                            onCardLike={onCardLike}
                            onCardDelete={onCardDelete}
                        />
                    ))
                }
            </section>

        </main>
    )

}



export default Main
