import { useEffect } from "react"

function PopupWithForm(props) {
    const { name, onClose, title, children, isOpen, buttonText } = props

    const popup = document.querySelector('.popup')

    useEffect(() => {
        function handleEscKey(e) {
            const isKyeEsc = e.key === 'Escape'
            isKyeEsc && onClose()
        }

        function handleClickOverlay(e) {
            const isOverflay = e.target === e.currentTarget
            isOverflay && onClose()
        }

        document.addEventListener('keydown', handleEscKey)
        popup.current.addEventListener('mousedown', handleClickOverlay)

        return () => {
            document.removeEventListener('keydown', handleEscKey)
            popup.current.removeEventListener('mousedown', handleClickOverlay)
        }
    })

    return (
        <section className={`popup ${isOpen && 'popup_opened'} popup_type_${props.name}`}>
           <div className="popup__container">
              <button type="button" className="popup__close-button link" aria-label="Закрыть" onClick={onClose}></button>
              <form className="popup__form-container" name={name} noValidate>
                  <fieldset className="popup__form-fieldset">
                       <h3 className="popup__title">{title}</h3>
                       
                       {children}
                       
                       <button type="submit" className="popup__submit-button popup__submit-button_edit-form">{buttonText}</button>
                    </fieldset>
             </form>
           </div>
           {/*<div className="popup__overlay"></div>*/}
        </section>
    )
}

export default PopupWithForm
